package org.example;import java.util.Scanner;public class ContaTerminal {    public static void main(String[] args) {        Integer numero;        String agencia;        String nomeCliente;        Double saldo;        Scanner scanner = new Scanner(System.in);        System.out.printf("Por favor, digite o número da conta!");        numero = scanner.nextInt();        System.out.printf("Por favor, digite o número da Agência!");        agencia = scanner.next();        System.out.printf("Por favor, digite o nome do Cliente!");        nomeCliente = scanner.next();        System.out.printf("Por favor, digite o saldo!");        saldo = scanner.nextDouble();        System.out.printf("Olá %s, obrigado por criar uma conta em nosso banco, sua agência é %s, conta %d e seu saldo %2f já está disponível para saque!", nomeCliente, agencia, numero, saldo);    }}